INSERT INTO self.self_articles (article_id, article_category, article_header, article_body, header_url, is_published, publishing_date, author_name, author_email, author_prsn_id, created_by, creation_date, last_update_by, last_update_date) VALUES (4, 'About Organisation', 'ABOUT ORGANISATION/INSTITUTION', '<div class="rho_form1" style="float:left;padding-left:20px;margin:5px;max-width:100%;min-height:170px;line-height: 162%;font-family:Arial !important;font-size:13px !important;">
                
    <div id="rho_form" style="min-height:477px;max-width:1050px;font-family:Tahoma !important;"> 
<fieldset style="padding:15px;"><h2 style="padding-left:25px;">
Welcome to the BOG Online Portal Suite of Applications/Solutions!</h2>
<div class="rho-postcontent rho-postcontent-0 clearfix">
<div style="float:left;padding:1px;margin-bottom:5px;"><div style="float:left">
<p>
<a href="http://www.bog.gov.gh/" target="_blank">
<img style="float:right; height:100px; margin-left:10px;" alt="BOG LOGO" src="cmn_images/bank_256.png">
</a>
<a href="http://www.bog.gov.gh/" target="_blank">
<img style="float:left; height:100px; margin-right:1px;" alt="BOGLOGO" src="cmn_images/BoG_Logo1.png">
</a>
BoG has developed an online Portal System which automates
the management of information across regulated institutions and facilitates information
flow between the regulator and the regulated.&nbsp;</p></div>
<div class="rho_form1" style="float:left;padding-left:10px;padding-bottom:5px;margin:5px;max-width:49%;min-height:170px;background-color:<?php echo #FFF; ?>;">
<h3>&nbsp;&nbsp;&nbsp;KEY FEATURES OF THE SYSTEM:</h3>
<ul style="list-style-image: url(''cmn_images/a_rt.gif'');list-style-position:outside;padding-left:40px;">
<li>User friendly Interface.</li>
<li>It can run on a variety of computer hardware and network configuration.</li>
<li>It employs a centralized robust database as a repository of information.</li>
<li>Data Import/Export to Excel and Word.</li>
<li>Supports multiple users.</li>
</ul>
</div>    
<div class="rho_form1" style="float:left;padding-left:10px;padding-bottom:5px;margin:5px;max-width:100%;min-height:170px;background-color:<?php echo #FFF; ?>;">
<h3>&nbsp;&nbsp;&nbsp;KEY FEATURES OF THE SYSTEM:</h3>
<ul style="list-style-image: url(''cmn_images/a_rt.gif'');list-style-position:outside;padding-left:40px;">
<li>Database backup and restore from application.</li>
<li>Robust application and information security management system.</li>
<li>In-built Templates for performing common tasks.</li>
<li>Easily Customized and Expandable to meet specific business needs</li>
<li>Display reports in Word, Html and PDF formats.</li>
</ul>
</div>    
</div>
</div></fieldset></div></div>', 'javascript: showArticleDetails({:articleID},''About Organisation'');', '1', '2015-11-08 15:50:47', 'System Administrator', 'webmaster@bog.gov.gh', -1, 1, '2015-11-08 15:50:47', 116, '2016-07-14 21:49:34');
INSERT INTO self.self_articles (article_id, article_category, article_header, article_body, header_url, is_published, publishing_date, author_name, author_email, author_prsn_id, created_by, creation_date, last_update_by, last_update_date) VALUES (1, 'Welcome Message', 'INTRODUCTION TO THE PORTAL', '<div class="rho_form1" style="max-width:{:introWdth}px;font-family:Arial !important; padding-left:20px">
                    <h3><a href="javascript: showArticleDetails({:articleID},''Notices/Announcements'');" style="font-weight:bold;text-decoration:underline;">INTRODUCTION TO THE PORTAL</a></h3>
                                <p style="line-height: 162%;">
                                <a href="http://www.bog.gov.gh/" target="_blank">
                                <img style="float:right; height:125px; margin-top:-5px; margin-left:10px;" alt="BANKS" src="cmn_images/bank_256.png">
                                </a>
                                <a href="http://www.bog.gov.gh/" target="_blank">
                                <img style="float:left; height:115px;  margin-top:-5px;margin-right:1px;" alt="BoGLOGO" src="cmn_images/BoG_Logo1.png">
                                </a>
                                This is the Information Resource Centre for Deposit Money Banks (DMBs) and Dedicated Electronic Money Issuers. 
                                It is the central depot for all Financial and Payment System Notices, Announcements, Articles and Research Papers. A registered Institution who logs in can view all directly Related Records held by the Bank of Ghana. <br>There is also a live and vibrant forum here where registered members can share knowledge
            and expertise on areas of importance. Articles, research papers and comments can be
            submitted to the BoG via this platform as well.</p></div>', 'javascript: showArticleDetails({:articleID},''Welcome Message'');', '1', '2015-11-04 23:56:57', 'System Administrator', 'info@bog.gov.gh', -1, 18, '2015-11-04 23:56:57', 116, '2016-08-03 10:40:48');

INSERT INTO self.self_articles (article_id, article_category, article_header, article_body, header_url, is_published, publishing_date, author_name, author_email, author_prsn_id, created_by, creation_date, last_update_by, last_update_date) VALUES (6, 'Operational Manuals', 'PASSWORD RESET ON PSB RETURNS PORTAL', '<font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;">?</span></font><span style="font-size: 13px;"><font face="helvetica, arial, verdana, sans-serif">Anytime you forget your Password, you can request for a new Password Reset Link by following these steps</font></span><div><font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;"><br></span></font></div><div><font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;"><span style="white-space:pre">    </span>1. Click on <a href="https://psdc.bog.gov.gh">PSB Returns</a> on the <a href="https://www.bog.gov.gh">Bank of Ghana Website</a></span></font></div><div><font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;"><span style="white-space:pre">    </span>2. Click on Request for New Password</span></font></div><div><font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;"><span style="white-space:pre">    </span>3. Enter your registered email address</span></font></div><div><font face="helvetica, arial, verdana, sans-serif"><span style="font-size: 13px;"><span style="white-space: pre;">    </span>4. Click on Send Password Reset Link</span></font></div>', '', '1', '2016-07-15 00:00:00', 'Portal Administrator', 'webmaster.bog.gov.gh', -1, 116, '2016-07-15 07:45:02', 116, '2016-07-15 08:01:11');

